/* eslint-disable jsx-a11y/anchor-is-valid */
import React from "react";
import { Link } from "react-router-dom";
import { shallowEqual, useSelector } from "react-redux";
import SVG from "react-inlinesvg";
import { useHistory } from "react-router-dom";
import { toAbsoluteUrl } from "../../../../_helpers";

export function QuickUser() {
  const history = useHistory();
  const user = useSelector((state) => state.auth.user, shallowEqual);

  const logoutClick = () => {
    const toggle = document.getElementById("kt_quick_user_toggle");
    if (toggle) {
      toggle.click();
    }
    history.push("/logout");
  };

  return (
    <div id="kt_quick_user" className="offcanvas offcanvas-left userProfilecanvas d-flex offcanvas">
      <div className="d-flex usermodalIconcontainer">
      1
      </div>
      <div className="d-flex w-100">
        <div className="d-flex">
          <div className="d-inline-block">
            <div className="d-flex">
              <div className="modaluserIcon">
                <span className="userIcon">SK</span>
                <span className="userName">Sivakumar</span>
              </div>
            </div>
            <ul>
              <li><a href="">Settings</a></li>
              <li><a href="">Import/Export</a></li>
              <li><a href="">People</a></li>
              <li><a href="">People</a></li>
            </ul>
          </div>
        </div>        
      </div>
    </div>
  );
}
